import React, { Component } from 'react';
import './action-cards.css';
import cardsData from './actionCardsData'
import {Link} from 'react-router-dom';

class ActionCards extends Component {
    constructor(){
        super()
        this.state = { actionCardData: cardsData}
    }
    
    render() { 
        const krDyRender = this.state.actionCardData.map((cardItem)=>{
            return(
                <Card 
                    imageType = {cardItem.logo} 
                    cardHeading={cardItem.heading}
                />
            )
        })
        
        return (
            <div className="reward-cards">
                <div className="reward-cards-wrapper">
                   
                </div>

                <div className="carousel-btns" style={{textAlign:'center'}}>
                    <button className='carouserl-track-button'>1</button>
                    <button className='carouserl-track-button'>2</button>
                    <button className='carouserl-track-button'>3</button>
                </div>

            </div>
         );
    }
}
export default ActionCards;

class Card extends Component {

    render() { 
        return ( 
            <div className="reward-card">
                <img src={this.props.imageType} alt="" className='reward-image'/>
                <h4>Universal Smartphone Foldable Selfie Stick</h4>
                <h5>50 ActBit coins required</h5>
                <h5>Draw date: 09/30/2008</h5>
                <Link to="/reward/rewards-individual">
                    <button className="btn view-rewards">VIEW DETAILS</button>
                </Link>
            </div>
         );
    }
}
