import React, { Component } from 'react'
import logo from './components/index/Header/logo-high-res.png'
import { Link } from 'react-router-dom'
import './Header.css'

class Header extends Component {
    state = { isLoggedin: false }
    render() { 
        const LinkStyle= {
            textDecoration: "none"
        }
        let sidebarButton = <div className="res-menu-btn">                  
                                <a className="fa fa-bars"></a>
                            </div>

        let iTakeActionLogo =  <div className="logo-image">                             
                        <a href="/"><img className="logo" src={logo} alt="iTakeAction"/></a>
                    </div>

        let navigationBar =   <div className="links">
                                <ul>
                                    <Link style={LinkStyle} to="/take-action"><li>TAKE ACTIONS</li></Link>
                                    <Link style={LinkStyle} to="/reward"><li>REWARD</li></Link>
                                    <Link style={LinkStyle} to="/submit-a-report"><li>SUBMIT A REPORT</li></Link>
                                    <Link style={LinkStyle} to="/blog"><li>BLOG</li></Link>
                                    <Link style={LinkStyle} to="/about-us"><li>ABOUT US</li></Link>
                                </ul>
                            </div>
        let searchBar = <i className='fa fa-search'></i>
        let loginButtons = <div className='side-btn'>
                            <a href="https://itakeactions.org/signin"><button className="login">LOGIN</button></a>
                            <a href="https://itakeactions.org/signup"><button className="signup">SIGN UP</button></a>
                        </div>
        let socialLinks = <div className="social-links">
                            <a href="#" className="fa fa-facebook"></a>
                            <a href="#" className="fa fa-instagram"></a>
                            <a href="#" className="fa fa-twitter"></a>
                            <a href="#" className="fa fa-youtube"></a>
                        </div>
        let userProfile = <Profile />

        return ( 
            <header style={{width:'100%', height:'90px'}}>
                <div className="header web-layout">
                    {iTakeActionLogo}
                    {navigationBar}
                    {userProfile}
                </div>
                <div className="header web-layout">
                    {sidebarButton}
                    {iTakeActionLogo}
                    {searchBar}
                    {userProfile}
                    <div className="responsive-navigation-menu">
                        {navigationBar}
                        {loginButtons}
                        {socialLinks}
                    </div>
                </div>
            </header>
         );
    }
}
 
export default Header;


class Profile extends Component {
    
    state = {menuOn:false}
    
    showProfileMenuHandler = ()=>{
            this.setState({menuOn: !this.state.menuOn})
    }
    
    render() { 
        let menuDisplay = {display:'none'}
        let btnStyle = 'fa fa-chevron-down'
        
        if(this.state.menuOn){
            menuDisplay.display="block"
            btnStyle = 'fa fa-chevron-up'
        }
        
        return ( 
            <div id='profileBar'>
                <div className='profile'>                              
                    <div className="profile-name" style={{display:'flex'}}>
                        <img src='' alt="profile pic" id="profile-pic"/>
                        <div>
                            <h4 className="user-name">Charles Omofowan</h4>
                            <p style={{color:"#60AB30"}} className="coins-in-wallet">400 ActBit coins</p>
                        </div>  
                        <i class={btnStyle} onClick={this.showProfileMenuHandler}></i>
                    </div>
                    <div className="profile-menu" style={menuDisplay}>
                        <ul className="profile-menu-items">
                            <li><i class="fa fa-user"></i>My Profile</li>
                            <li><i class="fa fa-money"></i>My Wallet</li>
                            <li><i class="fa fa-gear"></i>Setting</li>
                            <li><i class="fa fa-user-plus"></i>Invite Friends</li>
                            {/* <li id='logout' onClick={props.logout}><i class="fa fa-sign-out"></i>LOG OUT</li> */}
                        </ul>
                    </div>
                </div>
            </div>
         );
    }
}
